import { useState } from 'react'

import { AddedColors } from '../../content/Interface/ColorsInterface'

export default function AddColorForm(event: any) {
	const [colors, setColors] = useState([])
	const [AddedColor, setAddedColor] = useState({

	})

	const handleChange = (event: { target: { name: string; value: string } }) => {
		const { name, value } = event.target
		setAddedColor(AddedColor => ({
			...AddedColor,
			[name]: value,
		}))
	}

	const handleSubmit = (AddedColor) => {
		event.preventDefault()
		setColors(...colors , AddedColor)
		
		setAddedColor({ name: '', hex: '', rgb: '' })

	}
	console.log(colors)
	return (
		<form onSubmit={handleSubmit}>
			<label htmlFor='name'>Enter color name:</label>
			<br />
			<input type='text' id='name' name='name' value={AddedColor} onChange={handleChange} />
			<br />
			Enter Hex value:
			<br />
			<input type='text' name='' id='hexValue' />
			<br />
			<input type='submit' />
		</form>
	)
}
