import { useState } from 'react'

export default function AddColorForm() {
	const [colors, setColors] = useState(JSON.parse(localStorage.getItem('colors') || '[]'))
	const [error, setError] = useState('');
	const [rgbColors, setRgbColors] = useState({red: null, green: null, blue: null});
	const [hexColor, setHexColor] = useState()
	//const [addRgb, setAddedRgb] = useState('')
	//const [addHex, setAddedHex] = useState('')
	const [color, setColor] = useState({
		hex: '',
		red: '',
		green: '',
		blue: '',
	})
	console.log(colors)
	const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		const value = event.target.value
		const name = event.target.name

		if (
			name === 'hex' &&
			(value.indexOf('#') !== 0 || value.lastIndexOf('#') !== 0 || value.indexOf('#') !== value.lastIndexOf('#'))
		) {
			setError('Pleas write good code of color')
		} else if ((name === 'red' || name === 'green' || name === 'blue') && value.length > 3) {
			setError('Pleas write good code of color')
		} else {
			setError('')
			setColor({ ...color, [event.target.name]: event.target.value })
		}
	}

	const handleSubmit = (event: React.FormEvent) => {
		event.preventDefault()
		const newColors = [...colors, color]
		setColors(newColors)
		setColor({
			hex: '',
			red: '',
			green: '',
			blue: '',
		})
		localStorage.setItem('colors', JSON.stringify(newColors))
	}

	return (
		<>
			<form onSubmit={handleSubmit}>
				<label htmlFor='hex'>Enter HEX value:</label>
				<br />
				<input type='text' name='hex' value={color.hex} onChange={handleChange} required maxLength={7} />
				<h3>{error}</h3>
				<br />
				<br />
				<label htmlFor='rgbColor' id='rgbColor'>
					Enter RGB colors
				</label>
				<br />
				<label htmlFor='red'> RED </label>
				<input type='number' name='red' id='' onChange={handleChange} value={color.red} required />
				<label htmlFor='red'> GREEN </label>
				<input type='number' name='green' id='' onChange={handleChange} value={color.green} required />
				<label htmlFor='red'> BLUE </label>
				<input type='number' name='blue' id='' onChange={handleChange} value={color.blue} required />
				<input type='submit' />
			</form>
		</>
	)
}
