import { useState } from 'react'

import { AddedColors } from '../../content/Interface/ColorsInterface'

export default function AddColorForm(event: any) {
	const [AddedColor, setAddedColor] = useState({
		name: '',
		hex: '',
		rgb: '',
	})

	const handleChange = (event: { target: { name: string; value: string } }) => {
		const { name, value } = event.target
		setAddedColor(AddedColor => ({
			...AddedColor,
			[name]: value,
		}))
	}

	const handleSubmit = (event: React.SyntheticEvent) => {
		event.preventDefault()
		setAddedColor(AddedColor.hex ='', AddedColor.name=''
		)
	}
	console.log(AddedColor)
	return (
		<form onSubmit={handleSubmit}>
			<label htmlFor='name'>Enter color name:</label>
			<br />
			<input type='text' id='name' name='name' value={AddedColor.name} onChange={handleChange} />
			<br />
			Enter Hex value:
			<br />
			<input type='text' name='' id='hexValue' />
			<br />
			<input type='submit' />
		</form>
	)
}
