import { useEffect, useState } from 'react'

import { AddedColors } from '../../content/Interface/ColorsInterface'

export default function AddColorForm() {
	const [colors, setColors] = useState([{}])
	//const [addRgb, setAddedRgb] = useState('')
	//const [addHex, setAddedHex] = useState('')
	const [color, setColor] = useState({
		hex:'',
		rgb:''
	})

	const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		setColor({...color, [event.target.name] : event.target.value})
	}
	
	const handleSubmit = (event: React.FormEvent) => {
		event.preventDefault()
		setColors([...colors, color])
		setColor
	}
	console.log(colors)
	useEffect(() => {
		localStorage.setItem('colors', JSON.stringify(colors))
	}, [colors])

	return (
		<>
			<form onSubmit={handleSubmit}>
				<label htmlFor='hex'>Enter color name:</label>
				<input type='text' name='hex' value={color.hex} onChange={handleChange} />
				<label htmlFor='rgb' id='hex'>
					Enter Hex value:
				</label>
				<input type='text' name='rgb'value={color.rgb} onChange={handleChange} />
				<input type='submit' />
			</form>
		</>
	)
}
