import { useEffect, useState } from 'react'
import './FilterColorForm-style.scss'
export default function FilterColorForm() {
	const [chosenColors, setChosenColor] = useState([
		{
			hex: '',
			red: '',
			green: '',
			blue: '',
		},
	])

	useEffect(() => {
		const chosenColor = localStorage.getItem('colors')
		if (chosenColor) {
			const colorArray: [] = JSON.parse(chosenColor)
			console.log(colorArray)
			setChosenColor(colorArray)
		}
	}, [])

	return (
		<div className='filter-container'>
			<h2>Options:</h2>
			<form onSubmit={() => console.log('dupa')}>
				<div className='filter-container-label red'>
					<label htmlFor='choseRed'>"Red is "more" then 50"</label>
					<input type='checkbox' />
				</div>
				<div className='filter-container-label green'>
					<label htmlFor='chosGreen'>"Green is "more" then 50"</label>
					<input type='checkbox' />
				</div>
				<div className='filter-container-label blue'>
					<label htmlFor='chosBlue'>"Blue is "more" then 50"</label>
					<input type='checkbox' />
				</div>
			</form>
			{chosenColors?.map(color => (
				<>
					<h4>Color in HEX: {color.hex}</h4>
					<h3>
						Color in RGB :{' '}
						<h6>
							{color.red}:{color.green}:{color.blue}
						</h6>
					</h3>
					<button>Remove color</button>
				</>
			))}
		</div>
	)
}
