import { useEffect, useState } from 'react'

import { AddedColors } from '../../content/Interface/ColorsInterface'

export default function AddColorForm() {
	const [colors, setColors] = useState([''])
	//const [addRgb, setAddedRgb] = useState('')
	//const [addHex, setAddedHex] = useState('')
	const [color, setColor] = useState({
		hex
	})

	const handleChangeRgb = (event: React.ChangeEvent<HTMLInputElement>) => {
		setAddedRgb(event.target.value)
	}
	const handleChangeHex = (event: React.ChangeEvent<HTMLInputElement>) => {
		setAddedHex(event.target.value)
	}

	const handleSubmit = (event: React.FormEvent) => {
		event.preventDefault()
		setColors([...colors, addRgb, addHex])
		setAddedRgb('')
	}
	console.log(colors)
	useEffect(() => {
		localStorage.setItem('colors', JSON.stringify(colors))
	}, [colors])

	return (
		<>
			<form onSubmit={handleSubmit}>
				<label htmlFor='name'>Enter color name:</label>
				<input type='text' id='name' name='name' value={addRgb} onChange={handleChangeRgb} />
				<label htmlFor='hex' id='hex'>
					Enter Hex value:
				</label>
				<input type='text' name='' id='hexValue' value={addHex} onChange={handleChangeHex} />
				<input type='submit' />
			</form>
			{colors.map(color => {
				return <p>{color}</p>
			})}
		</>
	)
}
