import { useEffect, useState } from 'react'

export default function AddColorForm() {
	const [colors, setColors] = useState(JSON.parse(localStorage.getItem('colors') || '[]'))
	//const [addRgb, setAddedRgb] = useState('')
	//const [addHex, setAddedHex] = useState('')
	const [color, setColor] = useState({
		hex: '',
		rgb: '',
	})
	console.log(colors)
	const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		const value = event.target.value;
		if (value.includes)
		console.log(value)
		setColor({ ...color, [event.target.name]: event.target.value })
	}

	const handleSubmit = (event: React.FormEvent) => {
		event.preventDefault()
		setColors([...colors, color])
		setColor({
			hex: '',
			rgb: '',
		})
	}
	useEffect(() => {
		localStorage.setItem('colors', JSON.stringify(colors))
	}, [colors])

	return (
		<>
			<form onSubmit={handleSubmit}>
				<label htmlFor='hex'>Enter RGB value:</label>
				<input type='text' name='hex' value={color.hex} onChange={handleChange} required />
				<label htmlFor='rgb' id='hex'>
					Enter Hex value:
				</label>
				<input type='text' name='rgb' value={color.rgb} onChange={handleChange} required minLength={7} maxLength={7} />
				<input type='submit' />
			</form>
		</>
	)
}
