import { useEffect, useState } from 'react'

export default function AddColorForm() {
	const [colors, setColors] = useState(JSON.parse(localStorage.getItem('colors') || '[]'))
	const [error, setError] = useState('')
	//const [addRgb, setAddedRgb] = useState('')
	//const [addHex, setAddedHex] = useState('')
	const [color, setColor] = useState({
		hex: '',
		rgb: '',
	})
	console.log(colors)
	const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		const value = event.target.value
		if (
			color.hex &&
			value.indexOf('#') !== -1 &&
			(value.indexOf('#') !== 0 || value.lastIndexOf('#') !== 0 )
		) {
			setError('Pleas write good code of color')
		} else {
			setError('')
			setColor({ ...color, [event.target.name]: event.target.value })
		}
	}

	const handleSubmit = (event: React.FormEvent) => {
		event.preventDefault()
		setColors([...colors, color])
		setColor({
			hex: '',
			rgb: '',
		})
	}
	useEffect(() => {
		localStorage.setItem('colors', JSON.stringify(colors))
	}, [colors])

	return (
		<>
			<form onSubmit={handleSubmit}>
				<label htmlFor='hex'>Enter HEX value:</label>
				<br />
				<input type='text' name='hex' value={color.hex} onChange={handleChange} required maxLength={7} />
				<h3>{error}</h3>
				<br />
				<label htmlFor='rgb' id='hex'>
					Enter RGB value:
				</label>
				<br />
				<input type='text' name='rgb' value={color.rgb} onChange={handleChange} required maxLength={11} />
				<input type='submit' />
			</form>
		</>
	)
}
