import { useEffect, useState } from 'react'

import { AddedColors } from '../../content/Interface/ColorsInterface'

export default function AddColorForm(event: any) {
	const [colorsArray, setColor] = useState([])
	const [addedColor, setAddedColor] = useState('')

	const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		setAddedColor(event.target.value)
	}

	const handleSubmit = (addedColor) => {
		event.preventDefault()
		setAddedColor('')
	}

	console.log(colorsArray)
	return (
		<form onSubmit={handleSubmit}>
			<p>{localStorage.getItem('addedColors')}</p>
			<label htmlFor='name'>Enter color name:</label>
			<br />
			<input type='text' id='name' name='name' value={addedColor} onChange={handleChange} />
			<br />
			Enter Hex value:
			<br />
			<input type='text' name='' id='hexValue' />
			<br />
			<input type='submit' />
		</form>
	)
}
