import { useEffect, useState } from 'react'

import { AddedColors } from '../../content/Interface/ColorsInterface'

export default function AddColorForm() {
	const [colors, setColors] = useState([''])
	//const [addRgb, setAddedRgb] = useState('')
	//const [addHex, setAddedHex] = useState('')
	const [color, setColor] = useState({
		hex:'',
		rgb:''
	})

	const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		setAddedRgb({})
	}
	
	const handleSubmit = (event: React.FormEvent) => {
		event.preventDefault()
		setColors([...colors, addRgb, addHex])
		setAddedRgb('')
	}
	console.log(colors)
	useEffect(() => {
		localStorage.setItem('colors', JSON.stringify(colors))
	}, [colors])

	return (
		<>
			<form onSubmit={handleSubmit}>
				<label htmlFor='name'>Enter color name:</label>
				<input type='text' id='name' name='hex' value={color.hex} onChange={handleChange} />
				<label htmlFor='hex' id='hex'>
					Enter Hex value:
				</label>
				<input type='text' name='rgb' id='hexValue' value={color.rgb} onChange={handleChange} />
				<input type='submit' />
			</form>
			{colors.map(color => {
				return <p>{color}</p>
			})}
		</>
	)
}
