import { useEffect, useState } from 'react'

import { AddedColors } from '../../content/Interface/ColorsInterface'
import FormInput from '../FormInput/FormInput'

export default function AddColorForm() {
	const [colors, setColors] = useState([''])
	const [addRgb, setAddedColor] = useState('')
	const [addHex, setAddHex] = useState('')

	const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		setAddedColor(event.target.value)
	}

	const handleSubmit = (event: React.FormEvent) => {
		event.preventDefault()
		setColors([...colors, addRgb])
		setAddedColor('')
	}
	console.log(colors)
	useEffect(() => {
		localStorage.setItem('colors', JSON.stringify(colors))
	}, [colors])

	return (
		<>
			<form onSubmit={handleSubmit}>
				<FormInput placeholder='Enter color in Rgb' />
				<FormInput />
				<FormInput />
				<label htmlFor='name'>Enter color name:</label>
				<br />
				<br />
				<input type='text' id='name' name='name' value={addRgb} onChange={handleChange} />
				<br />
				<br />
				<label htmlFor='hex' id='hex'>
					Enter Hex value:
				</label>

				<br />
				<input type='text' name='' id='hexValue' />
				<br />
				<br />
				<input type='submit' />
			</form>
		</>
	)
}
