import { unescape } from 'querystring'
import { useState } from 'react'

const defaultRGBState = { red: undefined, green: undefined, blue: undefined }

export default function AddColorForm() {
	const [colors, setColors] = useState(JSON.parse(localStorage.getItem('colors') || '[]'))
	const [error, setError] = useState('')
	const [rgbColors, setRgbColors] = useState<{
		red: undefined | string
		green: undefined | string
		blue: undefined | string
	}>(defaultRGBState)
	const [hexColor, setHexColor] = useState<string>()

	const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
		const value = event.target.value
		const name = event.target.name

		if (
			name === 'hex' &&
			(value.indexOf('#') !== 0 || value.lastIndexOf('#') !== 0 || value.indexOf('#') !== value.lastIndexOf('#'))
		) {
			setError('Pleas write good code of color')
		} else if ((name === 'red' || name === 'green' || name === 'blue') && value.length > 3) {
			setError('Pleas write good code of color')
		} else {
			setError('')
			name === 'hex'
				? setHexColor(event.target.value)
				: setRgbColors(prev => ({ ...prev, [event.target.name]: event.target.value }))
		}
	}

	const handleSubmit = (event: React.FormEvent) => {
		event.preventDefault()
		const color = hexColor ?? rgbColors
		const newColors = [...colors, color]
		setColors(newColors)
		setRgbColors(defaultRGBState)
		setHexColor(undefined)
		localStorage.setItem('colors', JSON.stringify(newColors))
	}

	return (
		<>
			<form onSubmit={handleSubmit}>
				<label htmlFor='hex'>Enter HEX value:</label>
				<br />
				<input type='text' name='hex' value={hexColor} onChange={handleChange} required={Object.values(rgbColors)} maxLength={7} />
				<h3>{error}</h3>
				<br />
				<br />
				<label htmlFor='rgbColor' id='rgbColor'>
					Enter RGB colors
				</label>
				<br />
				<label htmlFor='red'> RED </label>
				<input type='number' name='red' id='' onChange={handleChange} value={rgbColors.red} required />
				<label htmlFor='red'> GREEN </label>
				<input type='number' name='green' id='' onChange={handleChange} value={rgbColors.green} required />
				<label htmlFor='red'> BLUE </label>
				<input type='number' name='blue' id='' onChange={handleChange} value={rgbColors.blue} required />
				<input type='submit' />
			</form>
		</>
	)
}
